import React, { useState } from 'react';
import { GoogleLogin, GoogleOAuthProvider } from '@react-oauth/google';

function App() {
  const [formData, setFormData] = useState({
    name: '',
    phone: '',
    birthday: '',
    email: ''
  });
  const [responseMsg, setResponseMsg] = useState('');

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      const res = await fetch('http://localhost:3001/api/signup', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json'
        },
        body: JSON.stringify(formData)
      });

      const json = await res.json();

      if (res.ok) {
        window.location.href = 'thanks.html';
      } else {
        setResponseMsg(json.message || 'Något gick fel.');
      }
    } catch (error) {
      console.error('Fel vid fetch:', error);
      setResponseMsg('Kunde inte ansluta till servern.');
    }
  };

  const handleGoogleSuccess = (credentialResponse) => {
    console.log('✅ Google-inloggad:', credentialResponse);
    // Här kan du skicka credentialResponse.credential till backend om du vill
  };

  const handleGoogleError = () => {
    console.log('❌ Google-inloggning misslyckades');
  };

  return (
    <GoogleOAuthProvider clientId="DIN_GOOGLE_CLIENT_ID">
      <div style={{ maxWidth: '400px', margin: '2rem auto', fontFamily: 'sans-serif' }}>
        <h2>Registrera dig</h2>
        <form onSubmit={handleSubmit}>
          <input
            name="name"
            placeholder="Namn"
            value={formData.name}
            onChange={handleChange}
            required
            style={{ width: '100%', marginBottom: '0.5rem' }}
          />
          <input
            name="phone"
            placeholder="Telefonnummer"
            value={formData.phone}
            onChange={handleChange}
            required
            style={{ width: '100%', marginBottom: '0.5rem' }}
          />
          <input
            name="birthday"
            placeholder="Födelsedatum (ÅÅÅÅ-MM-DD)"
            value={formData.birthday}
            onChange={handleChange}
            required
            style={{ width: '100%', marginBottom: '0.5rem' }}
          />
          <input
            name="email"
            type="email"
            placeholder="E-post"
            value={formData.email}
            onChange={handleChange}
            required
            style={{ width: '100%', marginBottom: '0.5rem' }}
          />
          <button type="submit" style={{ width: '100%' }}>Skicka</button>
        </form>

        <p style={{ color: 'red', marginTop: '1rem' }}>{responseMsg}</p>

        <hr style={{ margin: '2rem 0' }} />

        <h3>Eller logga in med Google</h3>
        <GoogleLogin onSuccess={handleGoogleSuccess} onError={handleGoogleError} />
      </div>
    </GoogleOAuthProvider>
  );
}

export default App;
